<script>
		var isClosed = true;
		function menu_to_exit(x) {
			x.classList.toggle("change");
		}
		
		function openNav(x) {
			if (isClosed === true) {
				x.style.width = "100%";
				isClosed = false;
			} else {
				x.style.width = "0%"
				isClosed = true;
			}
		}

		const sortBtn = document.getElementById("sort-btn");
		const sortBox = document.getElementById("sort-menu");
		const filterBtn = document.getElementById("filter-btn");
		const filterBox = document.getElementById("filter-menu");
		const ascPriceBtn = document.getElementById("ascPriceBtn");
		const descPriceBtn = document.getElementById("descPriceBtn");
		const ascRatingBtn = document.getElementById("ascRatingBtn");
		const descRatingBtn = document.getElementById("descRatingBtn");
		const list = document.getElementById("list");
		let sorted = false;
		let menuOpen = null;

		sortBtn.addEventListener("click", () => {
		if (menuOpen === "sort-menu") {
			document.getElementById("sort-menu").style.display = "none";
			menuOpen = null;
		} else {
			document.getElementById("sort-menu").style.display = "block";
			document.getElementById("filter-menu").style.display = "none";
			menuOpen = "sort-menu";
		}
		});

		filterBtn.addEventListener("click", () => {
		if (menuOpen === "filter-menu") {
			document.getElementById("filter-menu").style.display = "none";
			menuOpen = null;
		} else {
			document.getElementById("filter-menu").style.display = "block";
			document.getElementById("sort-menu").style.display = "none";
			menuOpen = "filter-menu";
		}
		});

		ascPriceBtn.addEventListener("click", function() {
		sortBox.style.display = "none";
		sorted = true;
		let items = Array.from(list.getElementsByTagName("LI"));
		items.sort(function(a, b) {
			return a.dataset.price - b.dataset.price;
		});
		items.forEach(function(item) {
			list.appendChild(item);
		});
		});

		descPriceBtn.addEventListener("click", function() {
		sortBox.style.display = "none";
		sorted = true;
		let items = Array.from(list.getElementsByTagName("LI"));
		items.sort(function(a, b) {
			return b.dataset.price - a.dataset.price;
		});
		items.forEach(function(item) {
			list.appendChild(item);
		});
		});

		ascRatingBtn.addEventListener("click", function() {
		sortBox.style.display = "none";
		sorted = true;
		let items = Array.from(list.getElementsByTagName("LI"));
		items.sort(function(a, b) {
			return a.dataset.rating - b.dataset.rating;
		});
		items.forEach(function(item) {
			list.appendChild(item);
		});
		});

		descRatingBtn.addEventListener("click", function() {
		sortBox.style.display = "none";
		sorted = true;
		let items = Array.from(list.getElementsByTagName("LI"));
		items.sort(function(a, b) {
			return b.dataset.rating - a.dataset.rating;
		});
		items.forEach(function(item) {
			list.appendChild(item);
		});
		});

		let selectedRatingValue = 0;

		function setRating(rating) {
			let stars = document.querySelectorAll('.star');
		stars.forEach(function(star) {
			star.classList.remove('red');
		});
		for (let i = 1; i <= rating; i++) {
			stars[i-1].classList.add('red');
		}
		selectedRatingValue = rating;
		}
		const venues = document.querySelectorAll(".venue");

		document.getElementById("update-btn").addEventListener("click", function() {
			var priceRange = document.getElementById("price-range").value;
			var selectedCategories = document.querySelectorAll("#category-filter input:checked");
			var selectedDays = document.querySelectorAll("#best-day input:checked");
			var venues = document.querySelectorAll(".venue");
			for (var i = 0; i < venues.length; i++) {
				var venue = venues[i];
				var rating = parseInt(venue.dataset.rating);
				var price = parseInt(venue.dataset.price);
				var category = venue.dataset.category;
				var bestDay = venue.dataset.bestDay;

				var shouldShow = true;
				if (selectedRatingValue > 0 && rating < selectedRatingValue) {
					shouldShow = false;
				}
				if (price > priceRange) {
					shouldShow = false;
				}
				if (selectedCategories.length > 0 && !Array.from(selectedCategories).some(cat => cat.value === category)) {
					shouldShow = false;
				}
				if (selectedDays.length > 0 && !Array.from(selectedDays).some(day => day.value === bestDay)) {
					shouldShow = false;
				}

				venue.classList.toggle("hidden", !shouldShow);
			}
		});
	</script>
	
